import createRoot from 'react-dom/client';

import swal from 'sweetalert';
import axios from 'axios';
import React, { useState } from 'react';
function App() {
  const [city, setCity] = useState('');
  const [weatherData, setWeatherData] = useState(null);

  const weatherApi = {
    key: 'b6bbebc26abee3f08ebbcd22a6be41bb',
    baseUrl: 'https://api.openweathermap.org/data/2.5/weather'
  };


  const handleKeyPress = (event) => {
    if (event.key === 'Enter') {
      getWeatherReport(city);
    }
  };

  const getWeatherReport = (city) => {
    fetch(`${weatherApi.baseUrl}?q=${city}&appid=${weatherApi.key}&units=metric`)
      .then((response) => response.json())
      .then((data) => {
        if (data.cod === '400') {
          swal('Empty Input', 'Please enter any city', 'error');
          reset();
        } else if (data.cod === '404') {
          swal('Bad Input', 'Entered city not found', 'warning');
          reset();
        } else {
          setWeatherData(data);
        }
      })
      .catch((error) => {
        console.error('Error fetching weather data:', error);
        swal('Error', 'Failed to fetch weather data', 'error');
      });
  };

  const showWeatherReport = () => {
    if (!weatherData) return null;

    const todayDate = new Date();

    return (
      <div>
        <div className="location-details">
          <div className="city" id="city">{`${weatherData.name}, ${weatherData.sys.country}`}</div>
          <div className="date" id="date">{dateManage(todayDate)}</div>
        </div>
        <div className="weather-status">
          <div className="temp" id="temp">{`${Math.round(weatherData.main.temp)}°C`}</div>
          <div className="weather" id="weather">{`${weatherData.weather[0].main} `}
            <i className={getIconClass(weatherData.weather[0].main)}></i>
          </div>
          <div className="min-max" id="min-max">{`${Math.floor(weatherData.main.temp_min)}°C (min) / ${Math.ceil(weatherData.main.temp_max)}°C (max)`}</div>
          <div id="updated_on">{`Updated as of ${getTime(todayDate)}`}</div>
        </div>
        <hr />
        <div className="day-details">
          <div className="basic">{`Feels like ${weatherData.main.feels_like}°C | Humidity ${weatherData.main.humidity}% <br /> Pressure ${weatherData.main.pressure} mb | Wind ${weatherData.wind.speed} KMPH`}</div>
        </div>
      </div>
    );
  };

  const getTime = (todayDate) => {
    const hour = addZero(todayDate.getHours());
    const minute = addZero(todayDate.getMinutes());
    return `${hour}:${minute}`;
  };

  const dateManage = (dateArg) => {
    const days = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];
    const months = ['January', 'February', 'March', 'April', 'May', 'June', 'July', 'August', 'September', 'October', 'November', 'December'];

    const year = dateArg.getFullYear();
    const month = months[dateArg.getMonth()];
    const date = dateArg.getDate();
    const day = days[dateArg.getDay()];

    return `${date} ${month} (${day}), ${year}`;
  };

  const changeBg = (status) => {
    const body = document.body;

    switch (status) {
      case 'Clouds':
        body.style.backgroundImage = 'url("img/clouds.jpg")';
        break;
      case 'Rain':
        body.style.backgroundImage = 'url("img/rainy.jpg")';
        break;
      case 'Clear':
        body.style.backgroundImage = 'url("img/clear.jpg")';
        break;
      case 'Snow':
        body.style.backgroundImage = 'url("img/snow.jpg")';
        break;
      case 'Sunny':
        body.style.backgroundImage = 'url("img/sunny.jpg")';
        break;
      case 'Thunderstorm':
        body.style.backgroundImage = 'url("img/thunderstrom.jpg")';
        break;
      case 'Drizzle':
        body.style.backgroundImage = 'url("img/drizzle.jpg")';
        break;
      case 'Mist':
      case 'Haze':
      case 'Fog':
        body.style.backgroundImage = 'url("img/mist.jpg")';
        break;
      default:
        body.style.backgroundImage = 'url("img/bg.jpg")';
        break;
    }
  };

  const getIconClass = (status) => {
    switch (status) {
      case 'Rain':
        return 'fas fa-cloud-showers-heavy';
      case 'Clouds':
        return 'fas fa-cloud';
      case 'Clear':
        return 'fas fa-cloud-sun';
      case 'Snow':
        return 'fas fa-snowman';
      case 'Sunny':
        return 'fas fa-sun';
      case 'Mist':
        return 'fas fa-smog';
      case 'Thunderstorm':
      case 'Drizzle':
        return 'fas fa-thunderstorm';
      default:
        return 'fas fa-cloud-sun';
    } };

  const reset = () => {
    setCity('');
  };

  const addZero = (i) => (i < 10 ? `0${i}` : i);


  return (
    <div>
    <input
      type="text"
      id="input-box"
      value={city}
      onChange={(e) => setCity(e.target.value)}
      onKeyPress={handleKeyPress}
      placeholder="Enter city name"
    />
    <button onClick={() => getWeatherReport(city)}>Get Weather</button>

    {showWeatherReport()}
  </div>
);
}

export default App;
