import createRoot from 'react-dom/client';

import swal from 'sweetalert';
import axios from 'axios';
import React, { useState } from 'react';
function App() {
  const [data, setData] = useState({})
  const [location, setLocation] = useState('')

  const url =`https://api.openweathermap.org/data/2.5/weather?q=${location}&units=imperial&appid=b6bbebc26abee3f08ebbcd22a6be41bb`

  const searchLocation = (event) => {
    if (event.key === 'Enter') {
      getWeatherReport(location);

    }
  }


  const getWeatherReport = async (city) => {
    try {
      const response = await axios.get(`${weatherApi.baseUrl}?q=${city}&appid=${weatherApi.key}&units=metric`);
      setWeatherData(response.data);
    } catch (error) {
      if (error.response.status === 400) {
        swal("Empty Input", "Please enter any city", "error");
      } else if (error.response.status === 404) {
        swal("Bad Input", "entered city didn't matched", "warning");
      }
    }
  }

  const dateManage = (dateArg) => {
    let days = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];
    let months = ['January', 'February', 'March', 'April', 'May', 'June', 'July', 'August', 'September', 'October', 'November', 'December'];
    let year = dateArg.getFullYear();
    let month = months[dateArg.getMonth()];
    let date = dateArg.getDate();
    let day = days[dateArg.getDay()];
    return `${date} ${month} (${day}) , ${year}`
  }

  const getIconClass = (classarg) => {
    let iconClass = '';
    if (classarg === 'Rain') {
      iconClass = 'fas fa-cloud-showers-heavy';
    } else if (classarg === 'Clouds') {
      iconClass = 'fas fa-cloud';
    } else if (classarg === 'Clear') {
      iconClass = 'fas fa-cloud-sun';
    } else if (classarg === 'Snow') {
      iconClass = 'fas fa-snowman';
    } else if (classarg === 'Sunny') {
      iconClass = 'fas fa-sun';
    } else if (classarg === 'Mist') {
      iconClass = 'fas fa-smog';
    } else if (classarg === 'Thunderstorm' || classarg === 'Drizzle') {
      iconClass = 'fas fa-thunderstorm';
    } else {
      iconClass = 'fas fa-cloud-sun';
    }
    return iconClass;
  }

  const changeBg = (status) => {
    if (status === 'Clouds') {
      document.body.style.backgroundImage = 'url(img/clouds.jpg)';
    } else if (status === 'Rain') {
      document.body.style.backgroundImage = 'url(img/rainy.jpg)';
    } else if (status === 'Clear') {
      document.body.style.backgroundImage = 'url(img/clear.jpg)';
    }
    else if (status === 'Snow') {
      document.body.style.backgroundImage = 'url(img/snow.jpg)';
    }
    else if (status === 'Sunny') {
      document.body.style.backgroundImage = 'url(img/sunny.jpg)';
    } else if (status === 'Thunderstorm') {
      document.body.style.backgroundImage = 'url(img/thunderstrom.jpg)';
    } else if (status === 'Drizzle') {
      document.body.style.backgroundImage = 'url(img/drizzle.jpg)';
    } else if (status === 'Mist' || status === 'Haze' || status === 'Fog') {
      document.body.style.backgroundImage = 'url(img/mist.jpg)';
    }

    else {
      document.body.style.backgroundImage = 'url(img/bg.jpg)';
    }
  }

  return (
    <div className="App">

      <div className="search">
        <h2>Get Weather Reports </h2>
        <div>  </div>
        <br></br>
        <input
          value={location}
          onChange={event => setLocation(event.target.value)}
          onKeyPress={searchLocation}
          placeholder='Enter Location'
          type="text" />
      </div>
      <div className="container">
        <div className="top">
          <div className="location">
            <p>{data.name}</p>
          </div>
          <div className="temp">
            {data.main ? <h1>{data.main.temp.toFixed()}°F</h1> : null}
          </div>
          <div className="description">
            {data.weather ? <p>{data.weather[0].main}</p> : null}
          </div>
        </div>

        {data.name !== undefined &&
          <div className="bottom">
            <div className="feels">
              {data.main ? <p className='bold'>{data.main.feels_like.toFixed()}°F</p> : null}
              <p>Feels Like</p>
            </div>
            <div className="humidity">
              {data.main ? <p className='bold'>{data.main.humidity}%</p> : null}
              <p>Humidity</p>
            </div>
            <div className="wind">
              {data.wind ? <p className='bold'>{data.wind.speed.toFixed()} MPH</p> : null}
              <p>Wind Speed</p>
            </div>
          </div>
        }



      </div>
    </div>

  );
}

export default App;
